import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ImageKitService } from './imagekit-public.service';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss'
})
export class AppComponent {
  uploadBoxes = Array(4).fill(0);
  capturedPhotos: string[] = Array(4).fill('');
  showCameraModal = false;
  showConfirmModal = false;
  showSuccessModal = false;
  currentBoxIndex = -1;
  videoElement: HTMLVideoElement | null = null;
  canvasElement: HTMLCanvasElement | null = null;
  stream: MediaStream | null = null;
  isUploading = false;
  uploadProgress = 0;

  constructor(private imagekitService: ImageKitService) {}

  async openCamera(boxIndex: number) {
    this.currentBoxIndex = boxIndex;
    this.showCameraModal = true;

    try {
      this.stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'environment', // Use back camera on mobile
          width: { ideal: 1280 },
          height: { ideal: 720 }
        } 
      });

      // Wait for the modal to be rendered
      setTimeout(() => {
        this.videoElement = document.getElementById('camera-video') as HTMLVideoElement;
        this.canvasElement = document.getElementById('camera-canvas') as HTMLCanvasElement;

        if (this.videoElement && this.stream) {
          this.videoElement.srcObject = this.stream;
          this.videoElement.play();
        }
      }, 100);
    } catch (error) {
      console.error('Error accessing camera:', error);
      alert('Unable to access camera. Please check permissions.');
      this.closeCamera();
    }
  }

  capturePhoto() {
    if (this.videoElement && this.canvasElement && this.currentBoxIndex >= 0) {
      const context = this.canvasElement.getContext('2d');
      if (context) {
        // Set canvas size to match video
        this.canvasElement.width = this.videoElement.videoWidth;
        this.canvasElement.height = this.videoElement.videoHeight;

        // Draw the current video frame to canvas
        context.drawImage(this.videoElement, 0, 0);

        // Convert canvas to data URL (base64 image)
        const photoDataUrl = this.canvasElement.toDataURL('image/jpeg', 0.8);

        // Store the photo
        this.capturedPhotos[this.currentBoxIndex] = photoDataUrl;

        // Close camera
        this.closeCamera();
      }
    }
  }

  closeCamera() {
    this.showCameraModal = false;
    this.currentBoxIndex = -1;

    // Stop the camera stream
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
      this.stream = null;
    }

    // Clear video element
    if (this.videoElement) {
      this.videoElement.srcObject = null;
      this.videoElement = null;
    }
  }

  retakePhoto(boxIndex: number) {
    this.capturedPhotos[boxIndex] = '';
    this.openCamera(boxIndex);
  }

  openConfirmModal() {
    const hasPhotos = this.capturedPhotos.some(photo => photo !== '');
    if (hasPhotos) {
      this.showConfirmModal = true;
    } else {
      alert('Please take at least one photo before uploading.');
    }
  }

  closeConfirmModal() {
    this.showConfirmModal = false;
  }

  async confirmUpload() {
    this.showConfirmModal = false;
    this.isUploading = true;
    this.uploadProgress = 0;

    try {
      const photosToUpload = this.capturedPhotos.filter(photo => photo !== '');

      // Simulate progress updates
      const progressInterval = setInterval(() => {
        if (this.uploadProgress < 90) {
          this.uploadProgress += 10;
        }
      }, 200);

      const downloadURLs = await this.imagekitService.uploadMultiplePhotos(photosToUpload);

      clearInterval(progressInterval);
      this.uploadProgress = 100;

      // Show success modal
      setTimeout(() => {
        this.isUploading = false;
        this.showSuccessModal = true;
        this.uploadProgress = 0;
      }, 500);

    } catch (error) {
      console.error('Upload failed:', error);
      this.isUploading = false;
      this.uploadProgress = 0;
      alert('Upload failed. Please try again.');
    }
  }

  closeSuccessModal() {
    this.showSuccessModal = false;
    // Reset photos after successful upload
    this.capturedPhotos = Array(4).fill('');
  }

  get hasPhotos() {
    return this.capturedPhotos.some(photo => photo !== '');
  }

  get photoCount() {
    return this.capturedPhotos.filter(photo => photo !== '').length;
  }
}
<div class="container">
  <header class="header">
    <h1 class="title">Partyclick</h1>
  </header>

  <main class="main-content">
    <p class="description">
      Upload 4 crazy fun clicks that you take in the event
    </p>

    <div class="upload-boxes">
      <div class="upload-box" *ngFor="let box of uploadBoxes; let i = index" (click)="openCamera(i)">
        <!-- Show captured photo if available -->
        <div *ngIf="capturedPhotos[i]; else showPlusIcon" class="captured-photo">
          <img [src]="capturedPhotos[i]" alt="Captured photo" class="photo-preview">
          <button class="retake-btn" (click)="retakePhoto(i); $event.stopPropagation()">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M1 4V10H7" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              <path d="M23 20V14H17" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              <path d="M20.49 9A9 9 0 0 0 5.64 5.64L1 10M23 14L18.36 18.36A9 9 0 0 1 3.51 15" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
        </div>

        <!-- Show plus icon if no photo -->
        <ng-template #showPlusIcon>
          <div class="plus-icon">
            <svg width="40" height="40" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 5V19M5 12H19" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </div>
        </ng-template>
      </div>
    </div>

    <!-- Upload Button -->
    <div class="upload-section">
      <button class="upload-btn" (click)="openConfirmModal()" [disabled]="!hasPhotos || isUploading">
        <span *ngIf="!isUploading">Upload {{ photoCount }} Photo{{ photoCount !== 1 ? 's' : '' }}</span>
        <span *ngIf="isUploading">Uploading...</span>
      </button>
    </div>
  </main>
</div>

<!-- Camera Modal -->
<div class="camera-modal" *ngIf="showCameraModal" (click)="closeCamera()">
  <div class="camera-container" (click)="$event.stopPropagation()">
    <div class="camera-header">
      <h3>Take Photo</h3>
      <button class="close-btn" (click)="closeCamera()">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M18 6L6 18M6 6L18 18" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
        </svg>
      </button>
    </div>

    <div class="camera-viewfinder">
      <video id="camera-video" autoplay muted playsinline></video>
      <canvas id="camera-canvas" style="display: none;"></canvas>
    </div>

    <div class="camera-controls">
      <button class="capture-btn" (click)="capturePhoto()">
        <svg width="40" height="40" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="12" cy="12" r="10" stroke="currentColor" stroke-width="2"/>
          <circle cx="12" cy="12" r="3" fill="currentColor"/>
        </svg>
      </button>
    </div>
  </div>
</div>

<!-- Confirmation Modal -->
<div class="confirm-modal" *ngIf="showConfirmModal" (click)="closeConfirmModal()">
  <div class="confirm-container" (click)="$event.stopPropagation()">
    <div class="confirm-header">
      <h3>Confirm Upload</h3>
    </div>

    <div class="confirm-content">
      <p>Would you like to submit all the photos you have taken?</p>
      <div class="photo-count">
        {{ photoCount }} photo{{ photoCount !== 1 ? 's' : '' }} ready to upload
      </div>
    </div>

    <div class="confirm-actions">
      <button class="cancel-btn" (click)="closeConfirmModal()">Cancel</button>
      <button class="confirm-btn" (click)="confirmUpload()">Yes, Upload</button>
    </div>
  </div>
</div>

<!-- Upload Progress Modal -->
<div class="progress-modal" *ngIf="isUploading">
  <div class="progress-container">
    <div class="progress-header">
      <h3>Uploading Photos</h3>
    </div>

    <div class="progress-content">
      <div class="progress-bar">
        <div class="progress-fill" [style.width.%]="uploadProgress"></div>
      </div>
      <p class="progress-text">{{ uploadProgress }}% Complete</p>
    </div>
  </div>
</div>

<!-- Success Modal -->
<div class="success-modal" *ngIf="showSuccessModal" (click)="closeSuccessModal()">
  <div class="success-container" (click)="$event.stopPropagation()">
    <div class="success-icon">
      <svg width="60" height="60" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M9 12L11 14L15 10M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
      </svg>
    </div>

    <div class="success-content">
      <h3>Upload Successful!</h3>
      <p>Your {{ photoCount }} photo{{ photoCount !== 1 ? 's' : '' }} have been uploaded successfully.</p>
    </div>

    <div class="success-actions">
      <button class="success-btn" (click)="closeSuccessModal()">Done</button>
    </div>
  </div>
</div>
